// Parse the agent output and extract the actual JSON
return $input.all().map(item => {
  let raw = item.json.output;

  // Remove markdown code block markers (```json and ```)
  raw = raw.replace(/^```json\n?/, '').replace(/```$/, '').trim();

  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (error) {
    parsed = { error: 'Failed to parse agent output', raw };
  }

  return { json: parsed };
});

